function Delivery(uber) {
    this._uber = uber;
    this.path = 'deliveries';
}

module.exports = Delivery;


Delivery.prototype.getAllDeliveries = function getAllDeliveries(stat, off, lmt, callback) {
    if (!stat) {
        return callback(new Error('Invalid status'));
    }

    return this._uber.get({
        url: this.path,
        params: {
            status: stat,
            offset: off,
            limit: lmt
        },
    }, callback);
};

Delivery.prototype.getAllQuotes = function getAllQuotes(location_obj, callback) {
    if (!location_obj) {
        return callback(new Error('Invalid location'));
    }
    return this._uber.post({
        url: this.path+'/quote',
        json:true,
        params: location_obj
    }, callback);
};
